class Solution {
private:
    bool non_increasing(vector<int>& s, int idx, int time){
        for(int i=idx-time; i<idx; i++){
            if(s[i]<s[i+1])
                return false;
        }
        return true;
    }

    bool non_decreasing(vector<int>& s, int idx, int time){
        for(int i=idx+time; i>idx; i--){
            if(s[i]<s[i-1])
                return false;
        }
        return true;
    }
public:
    vector<int> goodDaysToRobBank(vector<int>& security, int time) {
        if(2*time+1 > security.size())
            return vector<int>{};

        vector<int> ans;

        if(time == 0){
            for(int i=0; i<security.size(); i++)
                ans.push_back(i);
            return ans;
        }


        bool iflag = false, dflag = false;
        for(int day=time; day < security.size()-time; day++){
            if(!iflag)
                iflag = non_increasing(security, day, time);
            else
                iflag = (security[day] <= security[day-1]);

            if(!dflag)
                dflag = non_decreasing(security, day, time);
            else
                dflag = (security[day+time] >= security[day+time-1]);

            if(iflag && dflag)
                ans.push_back(day);
        }

        return ans;
    }
};
