class Solution {
public:
    long long smallestNumber(long long num) {
        if(num == 0) return num;
        long long abs_num = abs(num);
        long long res = 0;
        map<int, int> mp;
        while(abs_num > 0){
            mp[abs_num % 10]++;
            abs_num /= 10;
        }
        if(num > 0){
            int tmp;
            for(int i=1; i<10; i++){
                if(mp[i] > 0){
                    tmp = i;
                    mp[i]--;
                    break;
                }
            }
            res = tmp;
            for(int i=0; i<10; i++){
                while(mp[i] > 0){
                    res *= 10;
                    res += i;
                    mp[i]--;
                }
            }
        }else{
            for(int i=9; i>=0;i--){
                while(mp[i] > 0){
                    res *= 10;
                    res += i;
                    mp[i]--;
                }
            }
            res = -res;
        }
        return res;
    }
};
