class Solution {
public:
    vector<string> cellsInRange(string s) {
        vector<string> res;
        char c1 = s[0], c2;
        int r1 = 0, r2 = 0;
        int seperate;
        for(int i=0; i<s.size(); i++){
            if(s[i] == ':')
                seperate = i;
        }
        for(int i=1; i<seperate; i++){
            r1 *= 10;
            r1 += s[i] - '0';
        }
        c2 = s[seperate+1];
        for(int i=seperate+2; i<s.size(); i++){
            r2 *= 10;
            r2 += s[i] - '0';
        }
        for(int i=c1-'A'; i<=c2-'A'; i++){
            for(int j=r1; j<=r2; j++){
                string tmp = "";
                tmp += 'A' + i;
                tmp += '0' + j;
                res.push_back(tmp);
            }
        }
        return res;
    }
};

