class Solution {
public:
    long long minimalKSum(vector<int>& nums, int k) {
        int idx = 0;
        long long i = 1;
        long long res = 0;
        sort(nums.begin(), nums.end());
        /*
        for(int num: nums){
            cout<<num<<" ";
        }
        cout<<endl;*/
        while(k > 0){
            if(idx == nums.size()){
                //cout<<i<<endl;
                res += (i + i+k-1) * k / 2;
                k = 0;
            }
            else if(k >= nums[idx] - i){
                if(idx > 0 && nums[idx] == nums[idx-1]){
                    idx++;
                    continue;
                }
                k -= nums[idx] - i;
                res += (i + nums[idx]-1) * (nums[idx] - i) / 2;
                i = nums[idx] + 1;
                idx++;
            }else{
                res += (i + i+k-1) * k / 2;
                k = 0;
            }
        }
        return res;
    }
};
