class Solution {
public:
    int countHillValley(vector<int>& nums) {

        vector<int> tmp;

        tmp.push_back(nums[0]);

        for(auto num :nums) {
            if (num != tmp[tmp.size()-1])
                tmp.push_back(num);
        }

        int output = 0;

        for (int i = 0 ; i < tmp.size(); i++) {

            if ( i == 0) {
                continue;
            } else if (i == tmp.size()-1) {
                continue;
            } else {
                if (tmp[i] > tmp[i-1] && tmp[i] > tmp[i+1])
                    output ++;
                else if (tmp[i] < tmp[i-1] && tmp[i] < tmp[i+1])
                    output ++;
            }

        }
        return output;
    }
};

