class Solution {
public:
    int countCollisions(string directions) {

        int output = 0 ;

        int size = directions.size();

        vector<int> dp_left(size);

        dp_left[0] = 0;

        for (int i = 1 ; i < size; i++) {
            dp_left[i] = (dp_left[i-1] || directions[i-1] == 'S' || directions[i-1] == 'R')? 1 : 0;
        }

        vector<int> dp_right(size);

        dp_right[size-1] = 0;

        for (int i = size-2 ; i >=0; i--) {
            dp_right[i] = (dp_right[i+1] || directions[i+1] == 'S' || directions[i+1] == 'L')? 1 : 0;
        }

        for (int i = 0 ; i < size; i++) {
            if (directions[i] == 'L')
                output += dp_left[i] == 1? 1:0;
        }
        for (int i = size-1 ; i>=0 ; i--) {
            if (directions[i] == 'R')
                output += dp_right[i] == 1? 1:0;
        }



        return output ;

    }
};
